import React, { useState } from 'react';

export function DarkMode () {
  const [dark, setDarkMode] = useState(false);

  const handleToggle = () => {
    setDarkMode(!dark);
  }

  return (
    <div className={dark ? 'dark' : 'light'}>
      <button onClick={handleToggle}>
        {dark ? 'Light Mode' : 'Dark Mode'}
      </button>
    </div>
  );
};
